// micro-highlight ~1 kB â€“ adds class hljs + colourises keywords
document.addEventListener('DOMContentLoaded',()=>{
  document.querySelectorAll('pre code.hljs').forEach(b=>{
    let t=b.textContent;
    // very small brush for JS/Python/C-like
    t=t.replace(/\b(function|var|let|const|if|else|for|while|return|import|from|async|await)\b/g, '<span class="hljs-keyword">$1</span>');
    t=t.replace(/\b(\d[\d.]*)\b/g, '<span class="hljs-number">$1</span>');
    t=t.replace(/(".*?"|'.*?')/g, '<span class="hljs-string">$1</span>');
    t=t.replace(/\/\/.*$/gm, '<span class="hljs-comment">$&</span>');
    b.innerHTML=t;
  });
});
