// micro-marked ~2 kB â€“ converts markdown -> HTML
self.marked={parse:(s)=>
  s.replace(/^### (.*)/gm,'<h3>$1</h3>')
   .replace(/^## (.*)/gm,'<h2>$1</h2>')
   .replace(/^# (.*)/gm,'<h1>$1</h1>')
   .replace(/```(\w+)?\n([\s\S]*?)```/g,(m,l,c)=>
     `<pre><code class="hljs language-${l||''}">${c.trim()}</code></pre>`)
   .replace(/`([^`]+)`/g,'<code>$1</code>')
   .replace(/\*\*(.+?)\*\*/g,'<strong>$1</strong>')
   .replace(/\*(.+?)\*/g,'<em>$1</em>')
   .replace(/\n/g,'<br>')
};
